import React, { useState } from "react";

function App() {
   let i = 0;
   let turn = "";
  const [state, setstate] = useState(initialState)

   const [btnValue1, setbtnValue1] = useState("");
   const [btnValue2, setbtnValue2] = useState("");
   const [btnValue3, setbtnValue3] = useState("");
   // const [btnValue4, setbtnValue4] = useState("");
   // const [btnValue5, setbtnValue5] = useState("");
   // const [btnValue6, setbtnValue6] = useState("");
   // const [btnValue7, setbtnValue7] = useState("");
   // const [btnValue8, setbtnValue8] = useState("");
   // const [btnValue9, setbtnValue9] = useState("");

   const handleClick = (val) => {
      i % 2 === 0 ? (turn = "X") : (turn = "O");
      i++;
      console.log(i, turn);
      switch (val) {
         case 1:
            setbtnValue1(turn);
            break;
         case 2:
            setbtnValue2(turn);
            break;
         case 3:
            setbtnValue3(turn);
            break;
      }
   };
   return (
      <div>
         <h1>Game</h1>

         <button
            value={btnValue1}
            onClick={() => {
               handleClick(1);
            }}
         >
            {btnValue1}
         </button>

         <button
            value={btnValue1}
            onClick={() => {
               handleClick(2);
            }}
         >
            {btnValue2}{" "}
         </button>
         <button
            value={btnValue1}
            onClick={() => {
               handleClick(3);
            }}
         >
            {" "}
            {btnValue3}{" "}
         </button>
      </div>
   );
}

export default App;
