    calcBtn.push(
      <button onClick={e=>{setNumber(number+e.target.value)}}> value={item}</button>
    );
  });

  function handleClick() {
    console.log("sds");
    setNumber((number = 4));
    console.log(number);
  }
  return (
    <div>
      <div className="result">{number}</div>

      <div>
        <Key number={1} />
        <Key number={2} />
        <Key number={3} onClick={handleClick} />
        <Key number={4} />
        <Key number={5} />
      </div>
    </div>
  );
}

export default App;
