import React, { useState } from "react";
import "./App.css";

function App() {
  let [number, setNumber] = useState("");

  function handleClick(e) {
    setNumber(number + e);
    console.log(number);
  }

  function handleCalc() {
    setNumber(eval(number));
  }
  return (
    <div>
      <div class="calculator">
        <div class="input" id="input"></div>
        <div class="buttons">
          <div class="operators">
            <button
              onClick={(e) => {
                setNumber(number + e.target.value);
                console.log(number);
              }}
            >
              +
            </button>
            <button
              onClick={(e) => {
                setNumber(number + e.target.value);
                console.log(number);
              }}
            >
              -
            </button>
            <div>&times;</div>
            <div>&divide;</div>
          </div>
          <div class="leftPanel">
            <div class="numbers">
              <div  onClick={() => handleClick(7)}>7</div>
              <div  onClick={() => handleClick(8)}>8</div>
              <div  onClick={() => handleClick(9)}>9</div>
            </div>
            <div class="numbers">
              <div  onClick={() => handleClick(4)}>4</div>
              <div  onClick={() => handleClick(5)}>5</div>
              <div  onClick={() => handleClick()}>6</div>
            </div>
            <div class="numbers">
              <div onClick={() => handleClick(1)}> 1</div>
              <div onClick={() => handleClick(2)}>2</div>
              <div onClick={() => handleClick(3)}>3</div>
            </div>
            <div class="numbers">
              <div onClick={() => handleClick(0)}>0</div>
              <div onClick={() => handleClick(".")}>.</div>
              <div id="clear" onClick={() => setNumber("")}>
                C
              </div>
            </div>
          </div>
          <div class="equal" id="result">
            =
          </div>
        </div>
      </div>
      <form action="">
        <input type="text" />
      </form>
      <div className="result">{calcBtn}</div>
      {number} <br />
      <button
        onClick={() => {
          setNumber("");
        }}
      >
        clear
      </button>
      <button onClick={handleCalc}>=</button>
    </div>
  );
}

export default App;
