import React, { useState } from "react";
import "./App.css";

function App() {
  function computer() {
    let r = Math.floor(Math.random() * 3);
    setcomp((comp = r));
  }

  function handleClick(val) {
    sethuman((human = val));
    computer();
  }
  let [comp, setcomp] = useState(0);
  let [human, sethuman] = useState(0);
  return (
    <div>
      <div className="usercontainer">
        <button onClick={() => handleClick(0)}>
          <i id="rock" class="fa fa-hand-rock-o choice"></i> 0 Rock
        </button>

        <span><button onClick={() => handleClick(1)}>
          <i id="paper" class="fa fa-hand-paper-o choice"></i> Paper
        </button></span>

       <span> <button onClick={() => handleClick(2)}>
          <i id="scissors" class="fa fa-hand-scissors-o choice"></i> Scissors
        </button></span>
      </div>

      <div class="score-board">
        <div id="user-label" class="badge">
          user
        </div>
        <div id="computer-label" class="badge">
          comp
        </div>
        <span id="user-score"> {human}</span> : <span id="computer-score">{comp}</span>
      </div>
    </div>
  );
}

export default App;
