import React, { useState } from "react";
import "./App.css";

const api = {
  key: "4573c189d467ca1814c1c10000060792",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const [query, setquery] = useState("");
  const [weather, setweather] = useState({});
  const [value, setvalue] = useState("");

  function handleCopy(e) {
    e.preventDefault();
    setvalue(e.target.value);
  }

  const search = (evt) => {
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=kochi&APPID=4573c189d467ca1814c1c10000060792`)
      .then((res) => res.json())
      .then((result) => {
        setweather(result);
        setvalue("");
        console.log(result);
      });
  };

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  return (
    <div className="">
      <div className="search-box">
        <input type="text" className="search-bar" placeholder="Search..." onChange={handleCopy} value={value} />
        <button></button>
      </div>

      <div className="location-box">
        <div className="location">ds</div>
        <div className="date">{dateBuilder(new Date())}</div>
      </div>
      <h3>{value}</h3>
    </div>
  );
}

export default App;
