import React, { useState } from "react";
import "./App.css";

function App() {
  let [number, setNumber] = useState("");
  const calcBtn = [];
  let arr = [0, 1, 2, 3, 4, 5, 6, ".", "AC", "*", "/"].forEach((item) => {
    calcBtn.push(
      <button
        onClick={(e) => {
          setNumber(number + e.target.value);
          console.log(number);
        }}
        value={item}
        key={item}
      >
        {item}
      </button>
    );
  });
  function handleCalc() {
    setNumber(eval(number));
  }
  return (
    <div>
      <div class="calculator">
        <div class="input" id="input"></div>
        <div class="buttons">
          <div class="operators">
            <button
              onClick={(e) => {
                setNumber(number + e.target.value);
                console.log(number);
              }}
            >
              +
            </button>
            <button
              onClick={(e) => {
                setNumber(number + e.target.value);
                console.log(number);
              }}
            >
              -
            </button>
            <div>&times;</div>
            <div>&divide;</div>
          </div>
          <div class="leftPanel">
            <div class="numbers">
              <div>7</div>
              <div>8</div>
              <div>9</div>
            </div>
            <div class="numbers">
              <div>4</div>
              <div>5</div>
              <div>6</div>
            </div>
            <div class="numbers">
              <div>1</div>
              <div>2</div>
              <div>3</div>
            </div>
            <div class="numbers">
              <div>0</div>
              <div>.</div>
              <div id="clear">C</div>
            </div>
          </div>
          <div class="equal" id="result">
            =
          </div>
        </div>
      </div>
      <form action="">
        <input type="text" />
      </form>
      <div className="result">{calcBtn}</div>
      {number} <br />
      <button
        onClick={() => {
          setNumber("");
        }}
      >
        clear
      </button>
      <button onClick={handleCalc}>=</button>
    </div>
  );
}

export default App;
