import React, { useState } from "react";
import "./App.css";

function EachSquare(props) {
   return (
      <button className='square' onClick={props.onClick}>
         {props.value}
      </button>
   );
}

function Board() {
   const [arr, setarr] = useState(Array(9).fill(null));
   const handleClick = (index) => {
      if (arr[index]) return;
      const squares = [...arr];
      squares[index] = "X";
      setarr(squares);
   };

   const renderSquare = (i) => <Square value={props.squares[i]} onClick={() => props.onClick(i)} />;

   return (
      <div>
         <div className='board-row'>
            {renderSquare(0)}
            {renderSquare(1)}
            {renderSquare(2)}
         </div>
         <div className='board-row'>
            {renderSquare(3)}
            {renderSquare(4)}
            {renderSquare(5)}
         </div>
         <div className='board-row'>
            {renderSquare(6)}
            {renderSquare(7)}
            {renderSquare(8)}
         </div>
      </div>
   );
}

export default EachSquare;
