import React, { useState } from "react";
import "./App.css";

function App() {
  const [value, setvalue] = useState("");
  function handleClick(e) {
    e.preventDefault();
    setvalue(e.target.value);
    console.log("sd", e);
  }

  return (
    <div className="">
      <form action="" onChange={handleClick}>
        <input type="text" />

        <button> Submit</button>
      </form>

      <h3>{value}</h3>
    </div>
  );
}

export default App;
