   // const [btnValue8, setbtnValue8] = useState("");
   // const [btnValue9, setbtnValue9] = useState("");

   const handleClick = (val) => {
      i % 2 === 0 ? turn='X':turn='O';
      i+=1
      switch(val){
        case 1: setbtnValue1(turn)
        case 1: setbtnValue1(turn)
        case 1: setbtnValue1(turn)
      }
   };
   return (
      <div>
         <h1>Game</h1>

         <button
            value={btnValue1}
            onClick={() => {
               handleClick(1);
            }}
         >
            {btnValue1}
         </button>

         <button
            value={btnValue1}
            onClick={() => {
               handleClick(2);
            }}
         >
            {btnValue2}{" "}
         </button>
         <button
            value={btnValue1}
            onClick={() => {
               handleClick(3);
            }}
         >
            {" "}
            {btnValue3}{" "}
         </button>
      </div>
   );
}

export default App;
