import React, { useState } from "react";
import "./App.css";

function App() {
  function computer() {
    let r = Math.floor(Math.random() * 3);
    setcomp((comp = r));
  }

  function handleClick(val) {
    sethuman((human = val));
    computer();
  }
  let [comp, setcomp] = useState(0);
  let [human, sethuman] = useState(0);
  return (
    <div className="App">
      <button onClick={() => handleClick(0)}> 0 Rock </button>
      <button onClick={() => handleClick(1)}> 1 Paper </button>
      <button onClick={() => handleClick(2)}> 2 Scissors </button>
      <div>
        <h1>Result</h1>
        <p>
         , {comp}
        </p>
      </div>


      <div class="score-board">
      <div id="user-label" class="badge">user</div>
      <div id="computer-label" class="badge">comp</div>
      <span id="user-score"> {human}</span> : <span id="computer-score">0</span>
    </div>

    </div>
  );
}

export default App;
