import React, { useState } from "react";
import "./App.css";

function Board() {
   const [arr, setarr] = useState(Array(9).fill(null));
   const handleClick = (index) => {
      if (arr[index]) return;
      const squares = [...arr];
      squares[index] = "X";
      setarr(squares);
   };

   const renderSquare = (i) => (
      <>
         <button className='square' onClick={() => props.onClick(i)} value={props.squares[i]}>
            {props.value}
         </button>
      </>
   );

   return (
      <div>
         <div className='board-row'>
            {renderSquare(0)}
            {renderSquare(1)}
            {renderSquare(2)}
         </div>
         <div className='board-row'>
            {renderSquare(3)}
            {renderSquare(4)}
            {renderSquare(5)}
         </div>
         <div className='board-row'>
            {renderSquare(6)}
            {renderSquare(7)}
            {renderSquare(8)}
         </div>
      </div>
   );
}

export default EachSquare;
