import React, { useState } from "react";

function App() {
   let turn = "";

   const [arr, setarr] = useState(Array(9).fill(""));
   let [counter, setCounter] = useState(0);
   const [btnValue9, setbtnValue9] = useState("");
   const [btnValue1, setbtnValue1] = useState("");
   const [btnValue2, setbtnValue2] = useState("");
   const [btnValue3, setbtnValue3] = useState("");
   const [btnValue4, setbtnValue4] = useState("");
   const [btnValue5, setbtnValue5] = useState("");
   const [btnValue6, setbtnValue6] = useState("");
   const [btnValue7, setbtnValue7] = useState("");
   const [btnValue8, setbtnValue8] = useState("");


   const computerTurn = () => {
      console.log("inside computerTurn");
      let num = Math.floor(Math.random() * 10);
      console.log(num);
      while (arr[num] === 1) {
         console.log("again num is", num);
         num = Math.floor(Math.random() * 10 );
      }
      turn = "O";
      const squares = [...arr];
      squares[num] = 1;
      setarr(squares);
      console.log("arr inside comp is", arr);
      switch (num) {
         case 1:
            setbtnValue1(turn);

            break;
         case 2:
            setbtnValue2(turn);
            break;
         case 3:
            setbtnValue3(turn);
            break;
         case 4:
            setbtnValue4(turn);
            break;
         case 5:
            setbtnValue5(turn);
            break;
         case 6:
            setbtnValue6(turn);
            break;
         case 7:
            setbtnValue7(turn);
            break;
         case 8:
            setbtnValue8(turn);
            break;
         case 9:
            setbtnValue9(turn);
            break;
      }
   };

   const handleClick = (val) => {
      turn = "X";
      const squares = [...arr];
      squares[val] = 1;
      setarr(squares);
      setCounter((counter += 1));
      console.log(squares);
      // console.log(`btnValue${val}` === "");
      if (`btnValue${val}` != "") {
         switch (val) {
            case 1:
               setbtnValue1(turn);

               break;
            case 2:
               setbtnValue2(turn);
               break;
            case 3:
               setbtnValue3(turn);
               break;
            case 4:
               setbtnValue4(turn);
               break;
            case 5:
               setbtnValue5(turn);
               break;
            case 6:
               setbtnValue6(turn);
               break;
            case 7:
               setbtnValue7(turn);
               break;
            case 8:
               setbtnValue8(turn);
               break;
            case 9:
               setbtnValue9(turn);
               break;
         }
         computerTurn();
      }
   };
   return (
      <div>
         <h1>Game</h1>

         <button
            value={btnValue1}
            onClick={() => {
               handleClick(1);
            }}>
            {btnValue1}
         </button>

         <button
            value={btnValue2}
            onClick={() => {
               handleClick(2);
            }}>
            {btnValue2}
         </button>

         <button
            value={btnValue3}
            onClick={() => {
               handleClick(3);
            }}>
            {btnValue3}
         </button>

         <br />
         <button
            value={btnValue4}
            onClick={() => {
               handleClick(4);
            }}>
            {btnValue4}
         </button>

         <button
            value={btnValue5}
            onClick={() => {
               handleClick(5);
            }}>
            {btnValue5}
         </button>

         <button
            value={btnValue6}
            onClick={() => {
               handleClick(6);
            }}>
            {btnValue6}
         </button>

         <br />

         <button
            value={btnValue7}
            onClick={() => {
               handleClick(7);
            }}>
            {btnValue7}
         </button>

         <button
            value={btnValue8}
            onClick={() => {
               handleClick(8);
            }}>
            {btnValue8}
         </button>

         <button
            value={btnValue9}
            onClick={() => {
               handleClick(9);
            }}>
            {btnValue9}
         </button>
      </div>
   );
}

export default App;
