import React, { useState } from "react";
import "./App.css";

function App() {

  function computer() {
    setCount((count += 1));
  }

  function handleClick() {
    setCount((count += 1));
  }
  let [count, setCount] = useState(0);
  return (
    <div className="App">
      <button onClick={handleClick}> Rock {count}</button>
      <button onClick={handleClick}> Paper {count}</button>
      <button onClick={handleClick}> Scissors {count}</button>
    </div>
  );
}

export default App;
