import React, { useState } from "react";
import "./App.css";
import Key from "./Key";

function App() {
  let [number, setNumber] = useState("");
  const calcBtn = [];
  let arr = [0, 1, 2, 3, 4, 5, 6, ".", "AC"].forEach((item) => {
    calcBtn.push(
      <button
        onClick={(e) => {
          setNumber(number + e.target.value);
          console.log(number);
        }}
        value={item}
        key={item}
      >
        {item}
      </button>
    );
  });

  return (
    <div>
      <form action="">
        <input type="text" />
      </form>

      <div className="result">{calcBtn}</div>
      {number} <br />
 <button> onClick={()=>{setNumber(0)}}>clear</button>
    </div>
  );
}

export default App;
